sys_script.script:
(function executeRule(current, previous /*null when async*/) {

var incidents = [];
var counter = 0;	
	// Add your code here
var gr = new GlideRecord('incident');
	gr.addActiveQuery();
	gr.addQuery('priority', '<=', 2); //queries the incidents with priority values equal or less than 2 = 1 and 2 
	gr.query();
	while(gr.next()){
		counter++;
		incidents.push(gr.priority.toString()); //all incidents with the right filter are pushed in the array
	}
	for(var i = 0; i < incidents.length; i++) { //i evaluates the contents of the array. as long as there are more contents, i moves to the next place of the array
		gs.addInfoMessage(incidents[i] == '2');
		if(incidents[i] == '2'){
			i =  i - 1;
			incidents.splice(i, 1);
		}	
	}
		for(var u = 0; u < incidents.length; u++) {
		gs.addInfoMessage(incidents[u]);

		}

	//gs.addInfoMessage(incidents.length);
//gs.addInfoMessage('There are this many priority 1 and priority 2 at the moment: ' + counter + ' ' + incidents.length);

})(current, previous);

//<priority>2</priority> + priority 1
